Scripts.OnValueChanged:
local text = getglobal(this:GetName() .. "Value");
if (text) then
	local min, max = this:GetMinMaxValues();
	local value = this:GetValue();
	local green = (value - min) / (max - min);
	local red = (max - value) / (max - min);
	text:SetVertexColor(red, green, "0.0");
	if (string.find(this:GetName(), "HealPrioritySlider")) then
		text:SetText(value .. "%");
	elseif (string.find(this:GetName(), "PrioritySlider")) then
		text:SetText(value);
	else
		text:SetText(value .. "%");
	end
end
if (this:GetName() == "HealerGUIMaxOverhealSlider") then
	HEALER_MAX_OVERHEAL = this:GetValue() / 100.0;
elseif (this:GetName() == "HealerGUIPlayerHealPrioritySlider") then
	HEALER_PLAYER_PRI = this:GetValue() / 100.0;
elseif (this:GetName() == "HealerGUIPartyHealPrioritySlider") then
	HEALER_PARTY_PRI = this:GetValue() / 100.0;
elseif (this:GetName() == "HealerGUIPartypetHealPrioritySlider") then
	HEALER_PARTYPET_PRI = this:GetValue() / 100.0;
elseif (this:GetName() == "HealerGUIRaidHealPrioritySlider") then
	HEALER_RAID_PRI = this:GetValue() / 100.0;
elseif (this:GetName() == "HealerGUIRaidpetHealPrioritySlider") then
	HEALER_RAIDPET_PRI = this:GetValue() / 100.0;
elseif (string.find(this:GetName(), "HealerGUIHealPercentSlider")) then
	local spell = getglobal("HealerGUIHealspellEntry" .. this:GetID() .. "Text"):GetText();
	HEALER_HEAL_CLASSES[HEALER_CUR_HEAL_CLASS][spell] = this:GetValue() / 100.0;
elseif (string.find(this:GetName(), "HealerGUIBuffPrioritySlider")) then
	local buff = getglobal("HealerGUIBuffspellEntry" .. this:GetID() .. "Text"):GetText();
	HEALER_BUFF_CLASSES[HEALER_CUR_BUFF_CLASS][HEALER_CUR_UNIT_CLASS][buff] = this:GetValue() / 1.0;
elseif (string.find(this:GetName(), "HealerGUIDebuffPrioritySlider")) then
	local unit = getglobal("HealerGUIDebuffUnitEntry" .. this:GetID() .. "Text"):GetText();
	if (HEALER_DEBUFF_NEW[HEALER_CUR_DEBUFF]) then
		HEALER_DEBUFF_NEW[HEALER_CUR_DEBUFF][unit] = this:GetValue() / 1.0;
	elseif (HEALER_DEBUFF_LIST[HEALER_CUR_DEBUFF]) then
		HEALER_DEBUFF_LIST[HEALER_CUR_DEBUFF][unit] = this:GetValue() / 1.0;
	end
end

Scripts.OnClick:
local a = 1;
local frame = getglobal("HealerGUITabFrame" .. a);
while (frame) do
	if ("HealerGUITab" .. a == this:GetName()) then
		if (a == 3) then
			HealerGUIDebuffListScrollBar_Update();
		end
		frame:Show();
		getglobal("HealerGUITab" .. a):LockHighlight();
	else
		frame:Hide();
		getglobal("HealerGUITab" .. a):UnlockHighlight();
	end
	a = a + 1;
	frame = getglobal("HealerGUITabFrame" .. a);
end

if (this:GetChecked()) then
	for a = 1, 5 do
		getglobal("HealerGUIAutoHealclassCheckButton" .. a):SetChecked(0);
	end
	getglobal("HealerGUIAutoHealclassCheckButton" .. this:GetID()):SetChecked(1);
	HEALER_AUTOHEAL = getglobal("HealerGUIHealclassEntry" .. this:GetID() .. "Text"):GetText();
else
	HEALER_AUTOHEAL = nil;
end

if (this:GetChecked()) then
	for a = 1, 5 do
		getglobal("HealerGUIAutobuffclassCheckButton" .. a):SetChecked(0);
	end
	getglobal("HealerGUIAutobuffclassCheckButton" .. this:GetID()):SetChecked(1);
	HEALER_AUTOBUFF = getglobal("HealerGUIBuffclassEntry" .. this:GetID() .. "Text"):GetText();
else
	HEALER_AUTOBUFF = nil;
end

Scripts.OnMouseUp:
if (this.isMoving) then
	this:StopMovingOrSizing();
	this.isMoving = false;
end

Scripts.OnMouseDown:
if (((not this.isLocked) or (this.isLocked == 0)) and (arg1 == "LeftButton")) then
	this:StartMoving();
	this.isMoving = true;
end

Scripts.OnHide:
				HealerStatusHeal:Hide();
				HealerStatusBuff:Hide();
				HealerStatusDebuff:Hide();

				if (this.isMoving) then
					this:StopMovingOrSizing();
					this.isMoving = false;
				end

