local text = string.lower(HealerGUIClassEditBox:GetText());
if (HealerGUIClassText:GetText() == HEALERGUI_NEW_HEALCLASS) then
	if (not HEALER_HEAL_CLASSES) then
		HEALER_HEAL_CLASSES = {};
	end
	if (not HEALER_HEAL_CLASSES[text]) then
		HEALER_HEAL_CLASSES[text] = {};
	end
	HealerGUIHealclassScrollBar_Update();
elseif (HealerGUIClassText:GetText() == HEALERGUI_NEW_BUFFCLASS) then
	if (not HEALER_BUFF_CLASSES) then
		HEALER_BUFF_CLASSES = {};
	end
	if (not HEALER_BUFF_CLASSES[text]) then
		HEALER_BUFF_CLASSES[text] = {};
	end
	HealerGUIBuffclassScrollBar_Update();
elseif (HealerGUIClassText:GetText() == NUKERGUI_NEW_NUKECLASS) then
	if (not NUKER_NUKE_CLASSES) then
		NUKER_NUKE_CLASSES = {};
	end
	if (not NUKER_NUKE_CLASSES[text]) then
		local start, stop, nukeclass, a, b, c = string.find(text, "^(.+) %- (.+) %- (.+) %- (.+)$");
		if (not nukeclass) then
			start, stop, nukeclass, a, b = string.find(text, "^(.+) %- (.+) %- (.+)$");
		end
		if (not nukeclass) then
			start, stop, nukeclass, a = string.find(text, "^(.+) %- (.+)$");
		end
		if (nukeclass and a and b and NUKER_NUKE_CLASSES[nukeclass .. " - " .. a .. " - " .. b]) then
			-- ooh, copy the abilities from the parent
			NUKER_NUKE_CLASSES[text] = {};
			for priority, data in NUKER_NUKE_CLASSES[nukeclass .. " - " .. a .. " - " .. b] do
				NUKER_NUKE_CLASSES[text][priority] = data;
			end
		elseif (nukeclass and a and NUKER_NUKE_CLASSES[nukeclass .. " - " .. a]) then
			-- ooh, copy the abilities from the parent
			NUKER_NUKE_CLASSES[text] = {};
			for priority, data in NUKER_NUKE_CLASSES[nukeclass .. " - " .. a] do
				NUKER_NUKE_CLASSES[text][priority] = data;
			end
		elseif (nukeclass and NUKER_NUKE_CLASSES[nukeclass]) then
			-- ooh, copy the abilities from the parent
			NUKER_NUKE_CLASSES[text] = {};
			for priority, data in NUKER_NUKE_CLASSES[nukeclass] do
				NUKER_NUKE_CLASSES[text][priority] = data;
			end
		end
	end
	NukerGUINukeclass:SetText(text);
	NukerGUI_SetUp();
end
HealerGUIClass:Hide();

