Scripts.OnClick:
local curclass = NukerGUINukeclass:GetText();
local lastclass;
for nukeclass, data in NUKER_NUKE_CLASSES do
	if (nukeclass == curclass and lastclass) then
		NukerGUINukeclass:SetText(lastclass);
		NukerGUI_SetUp();
		return;
	end
	lastclass = nukeclass;
end
NukerGUINukeclass:SetText(lastclass);
NukerGUI_SetUp();

HealerGUIClassEditBox:SetText("");
HealerGUIClassText:SetText(NUKERGUI_NEW_NUKECLASS);
HealerGUIClass:Show();

local curclass = NukerGUINukeclass:GetText();
local nextclass;
local picknext;
for nukeclass, data in NUKER_NUKE_CLASSES do
	if (not nextclass or picknext) then
		nextclass = nukeclass;
		picknext = nil;
	end
	if (curclass == nukeclass) then
		picknext = 1;
	end
end
NukerGUINukeclass:SetText(nextclass);
NukerGUI_SetUp();

if (NUKER_NUKE_CLASSES and NukerGUINukeclass:GetText() and NUKER_NUKE_CLASSES[NukerGUINukeclass:GetText()]) then
	NUKER_NUKE_CLASSES[NukerGUINukeclass:GetText()] = nil;
	NukerGUINukeclass:SetText();
end
NukerGUI_SetUp();

