Scripts.OnClick:
-- we (don't) want to heal this person
local parentgroup = string.gsub(this:GetName(), "Slot%dCheckButton$", "");
local group = getglobal(parentgroup):GetID();
local parentslot = string.gsub(this:GetName(), "CheckButton$", "");
local slot = getglobal(parentslot):GetID();
Genesis_data["raid"][group][slot] = (this:GetChecked() or 0);

-- (un)check all buttons in this group
local parent = string.gsub(this:GetName(), "Label$", "");
local group = getglobal(parent):GetID();
if (getglobal(parent .. "Slot1CheckButton"):GetChecked()) then
    -- uncheck all
    for slot = 1, 5 do
        Genesis_data["raid"][group][slot] = 0;
    end
else
    -- check all
    for slot = 1, 5 do
        Genesis_data["raid"][group][slot] = 1;
    end
end
Genesis_SetRaidChecked();

Scripts.OnShow:
local parent = string.gsub(this:GetName(), "RankSlider$", "");
local spell = getglobal(parent .. "SpellText"):GetText();
this.variable = "Genesis_data[\"classes\"][Genesis_current_class][\"" .. spell .. "\"][\"Rank\"]";
this:SetMinMaxValues(0, table.getn(Genesis_spells[spell]));
this:SetValue(Genesis_data["classes"][Genesis_current_class][spell]["Rank"]);

local parent = string.gsub(this:GetName(), "PercentSlider$", "");
local spell = getglobal(parent .. "SpellText"):GetText();
this.variable = "Genesis_data[\"classes\"][Genesis_current_class][\"" .. spell .. "\"][\"Percent\"]";
this:SetValue(Genesis_data["classes"][Genesis_current_class][spell]["Percent"]);

Scripts.OnClick:
PanelTemplates_Tab_OnClick(Genesis_GUI);
local id = this:GetID();
Genesis_GUIClasses:Hide();
Genesis_GUIMouseKeys:Hide();
Genesis_GUIPriorities:Hide();
Genesis_GUIRaid:Hide();
Genesis_GUISettings:Hide();
if (id == 1) then
    Genesis_GUIClasses:Show();
elseif (id == 2) then
    Genesis_GUIMouseKeys:Show();
elseif (id == 3) then
    Genesis_GUIPriorities:Show();
elseif (id == 4) then
    Genesis_GUIRaid:Show();
    Genesis_SetRaidChecked();
elseif (id == 5) then
    Genesis_GUISettings:Show();
end

local text = Genesis_GUITextBoxEditBox:GetText();
if (not text or text == "") then
    return;
end
text = string.lower(text);
if (text == "load" or text == "save" or text == "delete" or text == "list" or text == "none") then
    C_Print("You may not name any heal class \"load\", \"save\", \"delete\", \"list\" or \"none\". Those names are reserved.", "|cffff0000");
    return;
end
if (not Genesis_data["classes"]) then
    Genesis_data["classes"] = {};
end
if (not Genesis_data["classes"][text]) then
    Genesis_data["classes"][text] = {};
end
Genesis_ClassDropDownMenuInitialize();
Genesis_ClassDropDownMenuUpdate(text)
Genesis_GUITextBox:Hide();
Genesis_GUITextBoxEditBox:SetText("");

Scripts.OnMouseUp:
if (this.isMoving) then
    this:StopMovingOrSizing();
    this.isMoving = false;
end

Scripts.OnMouseDown:
if (((not this.isLocked) or (this.isLocked == 0)) and (arg1 == "LeftButton")) then
    this:StartMoving();
    this.isMoving = true;
end

Scripts.OnHide:
if (this.isMoving) then
    this:StopMovingOrSizing();
    this.isMoving = false;
end

