Scripts.OnHide:
if (this.isMoving) then
    this:StopMovingOrSizing();
    this.isMoving = false;
end
for a = 1, 15 do
    local bar = getglobal("Genesis_GUIHealBars" .. a);
    if (bar:GetMinMaxValues() == 666) then
        getglobal("Genesis_GUIHealBars" .. a):Hide();
    end
end
Genesis_GUIHealCurrent:Hide();

Scripts.OnShow:
if (not Genesis_spellbook_checked) then
    Genesis_UpdateSpells();
end
if (Genesis_data["show_healing_all"] or Genesis_data["show_healing_me"]) then
    for a = 1, 15 do
        getglobal("Genesis_GUIHealBars" .. a):SetAlpha(1.0);
        getglobal("Genesis_GUIHealBars" .. a):Show();
    end
end
Genesis_GUIHealCurrent:SetAlpha(1.0);
Genesis_GUIHealCurrent:Show();

