Scripts.OnClick:
for group = 1, 8 do
    for slot = 1, 5 do
        Genesis_data["raid"][group][slot] = 1;
    end
end
Genesis_SetRaidChecked();

for group = 1, 8 do
    for slot = 1, 5 do
        Genesis_data["raid"][group][slot] = 0;
    end
end
Genesis_SetRaidChecked();

for group = 1, 8 do
    for slot = 1, 5 do
        Genesis_data["raid"][group][slot] = 1 - Genesis_data["raid"][group][slot];
    end
end
Genesis_SetRaidChecked();
